import './App.css';
import moment from 'moment';
import React, {useState} from 'react';
function App() {
  const api = {
    key: "32ba0bfed592484379e51106cef3f204",
    base: "https://api.openweathermap.org/data/2.5/"
  }

  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});
  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${query}&appid=32ba0bfed592484379e51106cef3f204`)
      .then(es => es.json())
        .then(esult => {
          setWeather(esult);
          setQuery('');
          console.log(esult);
        });
    }
  }
  const gettdate=(d,k)=> {
    let days = ["Sun", "Mon", "Tues", "Wed", "Thu", "Fri", "Sat"];
    let day = days[d.getDay()+k];
    return `${day}`
  }
  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sun", "Mon", "Tues", "Wed", "Thu", "Fri", "Sat"];
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }
  setInterval = () => {
    let time = new Date();
    let hour = time.getHours();
    let hoursIn12HrFormat = hour >= 13 ? hour %12: hour
    let minutes = time.getMinutes();
    let ampm = hour >=12 ? 'PM' : 'AM'

    return `${hoursIn12HrFormat}:${minutes} ${ampm}`
  }

  return (
<div>
<div class="container">
    <div class="current-info">

        <div class="date-container">
            <div class="time" id="time">
            { setInterval() }
            </div>
            <div class="date" id="date">
            { dateBuilder(new Date()) }
            </div>

            <div class="others" id="current-weather-items">
            {(typeof weather.city != "undefined") ? (
        <div>
<div class="weather-item">
<div>
<div>Humidity - {weather.list[0].main.humidity}%</div>
<div>Maximum Temperature - {weather.list[0].main.temp_max}°c</div>
<div>Minimum Temperature - {weather.list[0].main.temp_min}°c</div>
<div>Pressure - {weather.list[0].main.pressure}</div>
<div>Wind Speed -  {weather.list[0].wind.speed}</div>
<div>Sunrise - {moment(weather.city.sunrise * 1000).format('HH:mm ')} Sunset - {moment(weather.city.sunset * 1000).format('HH:mm ')}</div>
</div>

</div>
</div>) : ('')}                      
            </div>
        </div>
        <div class="place-container">
                <div class="time-zone" id="time-zone">Asia/Kolkata</div>
                <div id="country" class="country">IN</div>
            </div>
        <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
    </div>    
</div>
{(typeof weather.city != "undefined") ? (
<div class="future-forecast">
    <div class="today" id="current-temp">
        <div class="other">          
        <img src={`http://openweathermap.org/img/wn/${weather.list[0].weather[0].icon}@4x.png`} alt="weather icon" class="w-icon"/>
            <div class="day">{ gettdate(new Date(),0)}</div>
            <div class="temp">Night - 25.6&#176; C</div>
            <div class="temp">Day - 35.6&#176; C</div>
        </div>
    </div>
    <div class="weather-forecast" id="weather-forecast">
        <div class="weather-forecast-item">
            <div class="day">{ gettdate(new Date(),1)}</div>
            <img src={`http://openweathermap.org/img/wn/${weather.list[1].weather[0].icon}@2x.png`} alt="weather icon" class="w-icon"/>
            <div class="temp">Night - 25.6&#176; C</div>
            <div class="temp">Day - 35.6&#176; C</div>
        </div>
    </div>
</div>
) :('')}
</div>
 );
}
export default App;
